package fr.ekazuki.ekamurder.player;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Random;

public enum MurderSkin {
	SIPHANO("Siphano", "", 
			""),
	
	TRUMP("Trump", "",
		  ""),
	
	OBSTINATE("ObstinateM", "",
			  ""),
	
	AYPIERRE("aypierre", "",
			 ""),
	
	FRIGIEL("Frigiel", "",
			""),
	
	BOBLENNON("Boblennon", "",
			  ""),
	
	SUPERBRIOCHE("Superbrioche" , "",
			     ""),
	
	FANTA("TheFantasio974", "",
		  ""),
	
	ZEFUT("zefut", "",
		  ""),
	
	EKAZUKI("ekazuki", "",
			""),
	
	HOLLFROST("Hollfrost", "", 
			  ""),
	
	LOGORRHEIQUE("Logorrheique", "",
			     "");
	
	private String username;
	private String value;
	private String signature;
	private static final Random RANDOM = new Random();


	MurderSkin(String username, String value, String signature) {
		this.username = username;
		this.value = value;
		this.signature = signature;
	}
	
	public static List<MurderSkin> getMultipleRandom(int nbr) {
		if (nbr > 12 || nbr <= 0) {
			return null;
		}
		

		List<MurderSkin> values = new ArrayList<MurderSkin>(Arrays.asList(values()));
		List<MurderSkin> res = new ArrayList<MurderSkin>();
		
		for(int i = 0; i < nbr; i++) {
			res.add(values.remove(RANDOM.nextInt(values.size())));
		}
	
		return res;
	}
	
	public String getValue() {
		return this.value;
	}
	
	public String getUsername() {
		return this.username;
	}
	
	public String getSignature() {
		return this.signature;
	}
	
	public static MurderSkin getMurderSkin(String username) {
		for(MurderSkin skin : values()) {
			System.out.println(skin.getUsername() + " - " + username);
			if (skin.getUsername().equals(username)) {
				return skin;
			}
		}
		
		return null;
	}
}
